"use strict";
var enc_1 = require("../enc");
var crypt_1 = require("../crypt");
var hamming_1 = require("../hamming");
var testData = enc_1.decodeBase64('');
var subsets = function (l, a) {
    return [
        a.slice(0, l),
        a.slice(l, l + l)
    ];
};
var keySizes = [];
for (var keySize = 2; keySize < 40; ++keySize) {
    var s = subsets(keySize, testData);
    var d = hamming_1.hammingDistance(s[0], s[1]);
    keySizes.push({
        distance: d,
        size: keySize,
        normal: d / keySize
    });
}
keySizes.sort(function (a, b) { return a.normal - b.normal; });
var blocks = [];
for (var i = 0; i < keySizes.length; ++i) {
    var size = keySizes[i].size;
    var testSet = [];
    for (var j = 0; j < testData.length; ++j) {
        testSet[j % size] = testSet[j % size] || [];
        testSet[j % size].push(testData[j]);
    }
    blocks.push({
        keySize: size,
        data: testSet
    });
}
var key = [];
blocks.forEach(function (block) {
    var scores = [];
    var _loop_1 = function (i) {
        block.data.forEach(function (dataSet) {
            var msg = enc_1.encodeAscii(crypt_1.xor(i, dataSet));
            scores.push({
                key: i,
                message: msg,
                score: crypt_1.score(msg)
            });
        });
    };
    for (var i = 0; i < 256; ++i) {
        _loop_1(i);
    }
    scores.sort(function (a, b) { return b.score - a.score; });
    key.push(scores[0].key);
});
console.log(enc_1.encodeAscii(key));
